            <div className="text-sm text-primary font-medium flex items-center gap-1 hover-elevate rounded px-2 py-1 cursor-pointer" data-testid="link-full-pricing">
              Zobraziť celý cenník <ArrowRight className="h-4 w-4" />
            </div>
          </Link>
        </CardFooter>
      </Card>
    </section>
  );
}

