server/db.ts.content:
import pg from 'pg';
import { drizzle } from 'drizzle-orm/node-postgres';
import * as schema from "@shared/schema";

const { Pool } = pg;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ 
  connectionString: process.env.DATABASE_URL,
  ssl: process.env.NODE_ENV === 'production' ? { rejectUnauthorized: false } : false
});

export const db = drizzle(pool, { schema });

client/src/components/CommentsSection.ts.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Textarea } from "@/components/ui/textarea";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { MessageSquare, User, Calendar } from "lucide-react";
import { formatDate } from "@/lib/seo";
import type { Comment } from "@shared/schema";

interface CommentsSectionProps {
  postId: string;
}

export function CommentsSection({ postId }: CommentsSectionProps) {
  const { toast } = useToast();
  const [authorName, setAuthorName] = useState("");
  const [authorEmail, setAuthorEmail] = useState("");
  const [content, setContent] = useState("");

  const { data: comments = [], isLoading } = useQuery<Comment[]>({
    queryKey: ["/api/blog/posts", postId, "comments"],
  });

  const createCommentMutation = useMutation({
    mutationFn: async (commentData: { authorName: string; authorEmail: string; content: string }) => {
      return await apiRequest("POST", `/api/blog/posts/${postId}/comments`, commentData);
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/blog/posts", postId, "comments"] });
      setAuthorName("");
      setAuthorEmail("");
      setContent("");
      toast({
        title: "Komentár odoslaný",
        description: "Váš komentár bol odoslaný a čaká na schválenie.",
      });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Chyba",
        description: error.message || "Nepodarilo sa odoslať komentár.",
      });
    },
  });

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();

    if (!authorName.trim() || !authorEmail.trim() || !content.trim()) {
      toast({
        variant: "destructive",
        title: "Chyba",
        description: "Všetky polia sú povinné.",
      });
      return;
    }

    createCommentMutation.mutate({
      authorName: authorName.trim(),
      authorEmail: authorEmail.trim(),
      content: content.trim(),
    });
  };

  return (
    <div className="space-y-8">
      <Card>
        <CardHeader>
          <h2 className="font-serif text-2xl font-bold flex items-center gap-2">
            <MessageSquare className="h-6 w-6" />
            Komentáre ({comments.length})
          </h2>
        </CardHeader>
        <CardContent className="space-y-6">
          {isLoading ? (
            <div className="text-center text-muted-foreground py-4">
              Načítavam komentáre...
            </div>
          ) : comments.length === 0 ? (
            <div className="text-center text-muted-foreground py-4">
              Zatiaľ žiadne komentáre. Buďte prvý, kto pridá komentár!
            </div>
          ) : (
            <div className="space-y-4">
              {comments.map((comment) => (
                <Card key={comment.id} className="bg-muted/30">
                  <CardContent className="pt-4">
                    <div className="flex items-start gap-3">
                      <div className="h-10 w-10 rounded-full bg-primary/10 flex items-center justify-center flex-shrink-0">
                        <User className="h-5 w-5 text-primary" />
                      </div>
                      <div className="flex-1">
                        <div className="flex items-center gap-2 mb-2">
                          <span className="font-semibold text-sm">
                            {comment.authorName}
                          </span>
                          <span className="text-xs text-muted-foreground flex items-center gap-1">
                            <Calendar className="h-3 w-3" />
                            {formatDate(new Date(comment.createdAt))}
                          </span>
                        </div>
                        <p className="text-sm leading-relaxed">
                          {comment.content}
                        </p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              ))}
            </div>
          )}
        </CardContent>
      </Card>

      <Card>
        <CardHeader>
          <h3 className="font-serif text-xl font-bold">
            Pridať komentár
          </h3>
        </CardHeader>
        <CardContent>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <label htmlFor="comment-name" className="text-sm font-medium mb-2 block">
                  Meno *
                </label>
                <Input
                  id="comment-name"
                  type="text"
                  placeholder="Vaše meno"
                  value={authorName}
                  onChange={(e) => setAuthorName(e.target.value)}
                  required
                  data-testid="input-comment-name"
                />
              </div>
              <div>
                <label htmlFor="comment-email" className="text-sm font-medium mb-2 block">
                  Email *
                </label>
                <Input
                  id="comment-email"
                  type="email"
                  placeholder="vas@email.sk"
                  value={authorEmail}
                  onChange={(e) => setAuthorEmail(e.target.value)}
                  required
                  data-testid="input-comment-email"
                />
              </div>
            </div>
            <div>
              <label htmlFor="comment-content" className="text-sm font-medium mb-2 block">
                Komentár *
              </label>
              <Textarea
                id="comment-content"
                placeholder="Váš komentár..."
                value={content}
                onChange={(e) => setContent(e.target.value)}
                required
                rows={4}
                data-testid="textarea-comment-content"
              />
            </div>
            <div className="text-xs text-muted-foreground mb-4">
              * Váš komentár bude zverejnený po schválení administrátorom.
            </div>
            <Button 
              type="submit" 
              disabled={createCommentMutation.isPending}
              data-testid="button-submit-comment"
            >
              {createCommentMutation.isPending ? "Odosiela sa..." : "Odoslať komentár"}
            </Button>
          </form>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/components/GoogleReviews.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Star, MapPin, Phone, Globe, ExternalLink } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";

type GoogleReview = {
  author_name: string;
  author_url?: string;
  language?: string;
  profile_photo_url?: string;
  rating: number;
  relative_time_description: string;
  text: string;
  time: number;
};

type LocationData = {
  name: string;
  rating: number;
  userRatingsTotal: number;
  address: string;
  phone: string;
  website: string;
};

type GoogleReviewsData = {
  reviews: GoogleReview[];
  location: LocationData | null;
  message?: string;
};

export function GoogleReviews() {
  const { data, isLoading } = useQuery<GoogleReviewsData>({
    queryKey: ["/api/google-reviews"],
  });

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="h-32 bg-muted animate-pulse rounded-lg" />
        <div className="h-48 bg-muted animate-pulse rounded-lg" />
      </div>
    );
  }

  if (!data || (!data.reviews.length && !data.location)) {
    return null;
  }

  const renderStars = (rating: number) => {
    return (
      <div className="flex gap-0.5" data-testid={`stars-rating-${rating}`}>
        {[1, 2, 3, 4, 5].map((star) => (
          <Star
            key={star}
            className={`h-4 w-4 ${
              star <= rating
                ? "fill-yellow-400 text-yellow-400"
                : "text-muted-foreground/30"
            }`}
          />
        ))}
      </div>
    );
  };

  return (
    <div className="space-y-8">
      {data.location && (
        <Card>
          <CardHeader>
            <CardTitle className="font-serif text-2xl flex items-center gap-2">
              Google Hodnotenia
              <Badge variant="secondary" className="text-sm font-normal">
                {data.location.rating.toFixed(1)} / 5.0
              </Badge>
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="flex flex-wrap gap-6">
              {data.location.address && (
                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <MapPin className="h-4 w-4" />
                  <span data-testid="text-location-address">{data.location.address}</span>
                </div>
              )}
              {data.location.phone && (
                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <Phone className="h-4 w-4" />
                  <a 
                    href={`tel:${data.location.phone.replace(/\s/g, '')}`}
                    className="hover:text-primary transition-colors"
                    data-testid="link-location-phone"
                  >
                    {data.location.phone}
                  </a>
                </div>
              )}
              {data.location.website && (
                <div className="flex items-center gap-2 text-sm text-muted-foreground">
                  <Globe className="h-4 w-4" />
                  <a 
                    href={data.location.website}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="hover:text-primary transition-colors flex items-center gap-1"
                    data-testid="link-location-website"
                  >
                    Webová stránka
                    <ExternalLink className="h-3 w-3" />
                  </a>
                </div>
              )}
            </div>

            <div className="flex items-center gap-2 pt-2">
              {renderStars(Math.round(data.location.rating))}
              <span className="text-sm text-muted-foreground" data-testid="text-total-reviews">
                ({data.location.userRatingsTotal} hodnotení)
              </span>
            </div>
          </CardContent>
        </Card>
      )}

      {data.reviews.length > 0 && (
        <div className="space-y-4">
          <h3 className="font-serif text-xl font-semibold">
            Nedávne recenzie
          </h3>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {data.reviews.slice(0, 6).map((review, index) => (
              <Card key={index} className="hover-elevate" data-testid={`card-review-${index}`}>
                <CardContent className="pt-6 space-y-3">
                  <div className="flex items-start justify-between gap-2">
                    <div className="flex-1 min-w-0">
                      <div className="font-medium truncate" data-testid={`text-reviewer-${index}`}>
                        {review.author_name}
                      </div>
                      <div className="text-xs text-muted-foreground">
                        {review.relative_time_description}
                      </div>
                    </div>
                    {review.author_url && (
                      <Button
                        variant="ghost"
                        size="icon"
                        asChild
                        className="flex-shrink-0"
                      >
                        <a
                          href={review.author_url}
                          target="_blank"
                          rel="noopener noreferrer"
                          data-testid={`link-reviewer-profile-${index}`}
                        >
                          <ExternalLink className="h-4 w-4" />
                        </a>
                      </Button>
                    )}
                  </div>

                  {renderStars(review.rating)}

                  {review.text && (
                    <p className="text-sm text-muted-foreground line-clamp-4" data-testid={`text-review-content-${index}`}>
                      {review.text}
                    </p>
                  )}
                </CardContent>
              </Card>
            ))}
          </div>

          {data.reviews.length > 6 && (
            <div className="text-center pt-4">
              <Button
                variant="outline"
                asChild
                data-testid="button-view-all-reviews"
              >
                <a
                  href={`https://search.google.com/local/reviews?placeid=${process.env.GOOGLE_PLACE_ID || 'ChIJAbCdYpBfbEcRYXZ3z0vLPPw'}`}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="inline-flex items-center gap-2"
                >
                  Zobraziť všetky recenzie na Google
                  <ExternalLink className="h-4 w-4" />
                </a>
              </Button>
            </div>
          )}
        </div>
      )}
    </div>
  );
}

client/src/pages/AdminComments.tsx.content:
import { useState, useEffect } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { useToast } from "@/hooks/use-toast";
import { queryClient } from "@/lib/queryClient";
import { MessageSquare, Check, Calendar, User, FileText, Lock } from "lucide-react";
import { formatDate } from "@/lib/seo";
import { Link } from "wouter";

interface PendingComment {
  id: string;
  postId: string;
  postTitle: string;
  postSlug: string;
  authorName: string;
  authorEmail: string;
  content: string;
  createdAt: string;
  approved: boolean;
}

export default function AdminCommentsPage() {
  const { toast } = useToast();
  const [adminKey, setAdminKey] = useState("");
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    // Security: Only use localStorage (no URL params to prevent credential leaks)
    const savedKey = localStorage.getItem("admin_key");

    if (savedKey) {
      setAdminKey(savedKey);
      setIsAuthenticated(true);
    }
  }, []);

  const { data: pendingComments = [], isLoading } = useQuery<PendingComment[]>({
    queryKey: ["/api/comments/pending"],
    enabled: isAuthenticated && !!adminKey,
    queryFn: async () => {
      const response = await fetch(`/api/comments/pending`, {
        headers: {
          'x-admin-key': adminKey,
        },
      });

      if (!response.ok) {
        throw new Error('Failed to fetch pending comments');
      }

      return response.json();
    },
  });

  const approveCommentMutation = useMutation({
    mutationFn: async (commentId: string) => {
      const response = await fetch(`/api/comments/${commentId}/approve`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
          'x-admin-key': adminKey,
        },
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to approve comment');
      }

      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/comments/pending"] });
      toast({
        title: "Komentár schválený",
        description: "Komentár bol úspešne schválený a je teraz viditeľný.",
      });
    },
    onError: (error: any) => {
      toast({
        variant: "destructive",
        title: "Chyba",
        description: error.message || "Nepodarilo sa schváliť komentár.",
      });
    },
  });

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (adminKey.trim()) {
      setIsAuthenticated(true);
      localStorage.setItem("admin_key", adminKey);
    } else {
      toast({
        variant: "destructive",
        title: "Chyba",
        description: "Zadajte admin kľúč.",
      });
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setAdminKey("");
    localStorage.removeItem("admin_key");
  };

  if (!isAuthenticated) {
    return (
      <div className="container mx-auto max-w-7xl px-4 md:px-6 py-12">
        <div className="max-w-md mx-auto">
          <Card>
            <CardHeader>
              <div className="flex items-center gap-3 mb-2">
                <Lock className="h-6 w-6 text-primary" />
                <h1 className="font-serif text-2xl font-bold">
                  Admin Prihlásenie
                </h1>
              </div>
              <p className="text-sm text-muted-foreground">
                Zadajte admin kľúč pre prístup k správe komentárov
              </p>
            </CardHeader>
            <CardContent>
              <form onSubmit={handleLogin} className="space-y-4">
                <div>
                  <label htmlFor="admin-key" className="text-sm font-medium mb-2 block">
                    Admin Kľúč
                  </label>
                  <Input
                    id="admin-key"
                    type="password"
                    placeholder="Zadajte admin kľúč..."
                    value={adminKey}
                    onChange={(e) => setAdminKey(e.target.value)}
                    required
                    data-testid="input-admin-key"
                  />
                </div>
                <Button type="submit" className="w-full" data-testid="button-admin-login">
                  Prihlásiť sa
                </Button>
              </form>
              <div className="mt-4 text-xs text-muted-foreground">
                Admin kľúč musí byť nastavený v prostredí ADMIN_KEY pre bezpečnosť.
              </div>
            </CardContent>
          </Card>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto max-w-7xl px-4 md:px-6 py-12">
      <div className="max-w-4xl mx-auto">
        <header className="mb-8">
          <div className="flex items-center justify-between mb-4">
            <h1 className="font-serif text-4xl font-bold">
              Správa komentárov
            </h1>
            <Button variant="outline" size="sm" onClick={handleLogout} data-testid="button-admin-logout">
              Odhlásiť sa
            </Button>
          </div>
          <p className="text-muted-foreground">
            Prehliadajte a schvaľujte komentáre čakajúce na moderáciu
          </p>
        </header>

        {isLoading ? (
          <Card>
            <CardContent className="py-12 text-center">
              <div className="text-muted-foreground">
                Načítavam neschválené komentáre...
              </div>
            </CardContent>
          </Card>
        ) : pendingComments.length === 0 ? (
          <Card>
            <CardContent className="py-12 text-center">
              <MessageSquare className="h-12 w-12 mx-auto mb-4 text-muted-foreground" />
              <h2 className="font-serif text-xl font-bold mb-2">
                Žiadne neschválené komentáre
              </h2>
              <p className="text-muted-foreground">
                Momentálne nie sú žiadne komentáre čakajúce na schválenie.
              </p>
            </CardContent>
          </Card>
        ) : (
          <div className="space-y-4">
            {pendingComments.map((comment) => (
              <Card key={comment.id} data-testid={`pending-comment-${comment.id}`}>
                <CardHeader className="space-y-3">
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex-1">
                      <div className="flex items-center gap-2 mb-2">
                        <Badge variant="outline">Neschválené</Badge>
                        <span className="text-xs text-muted-foreground flex items-center gap-1">
                          <Calendar className="h-3 w-3" />
                          {formatDate(new Date(comment.createdAt))}
                        </span>
                      </div>
                      <Link href={`/blog/${comment.postSlug}`}>
                        <div className="flex items-center gap-2 text-sm text-muted-foreground hover:text-primary transition-colors" data-testid={`link-post-${comment.postSlug}`}>
                          <FileText className="h-4 w-4" />
                          <span>{comment.postTitle}</span>
                        </div>
                      </Link>
                    </div>
                    <Button
                      size="sm"
                      onClick={() => approveCommentMutation.mutate(comment.id)}
                      disabled={approveCommentMutation.isPending}
                      data-testid={`button-approve-${comment.id}`}
                    >
                      <Check className="h-4 w-4 mr-2" />
                      Schváliť
                    </Button>
                  </div>
                </CardHeader>
                <CardContent className="space-y-3">
                  <div className="flex items-center gap-2 text-sm">
                    <User className="h-4 w-4 text-muted-foreground" />
                    <span className="font-medium" data-testid={`text-comment-author-${comment.id}`}>{comment.authorName}</span>
                    <span className="text-muted-foreground">
                      ({comment.authorEmail})
                    </span>
                  </div>
                  <div className="bg-muted/30 rounded-lg p-4">
                    <p className="text-sm leading-relaxed" data-testid={`text-comment-content-${comment.id}`}>
                      {comment.content}
                    </p>
                  </div>
                </CardContent>
              </Card>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
